import { useState } from "react";

const CourseDetails = () => {
  const [courseDetails, setCourseDetails] = useState(null);
  const [courseID, setCourseID] = useState("");
  const [editMode, setEditMode] = useState("");
  const [editedDetails, setEditedDetails] = useState({
    title: "",
    description: "",
    instructor: "",
    duration: 0,
    availableSlots: 0,
  });

  const handleSearch = () => {
    try {
      fetch(`/api/courses/${courseID}`).then((response) => {
        if (response.ok) {
          console.log("Course Existing");
          return response.json()
        } else {
          console.log("error response empty");
        }
      })
      .then(data => {
            setCourseDetails(data)
      });
    } catch (error) {
        console.log(error)
    }
  };

  return (
    <div>
      <h1>Course Details</h1>
      <input
        type="text"
        name="courseID"
        placeholder="Enter Course ID"
        value={courseID}
        onChange={(e) => setCourseID(e.target.value)}
      />
      <button onClick={handleSearch}>Search</button>
      {courseDetails && (
        <div>
          {editMode ? (
            <div>We are in edit mode</div>
          ) : (
            <div>
              <p>Title: {courseDetails.title}</p>
              <p>Description: {courseDetails.description}</p>
              <p>Instructor: {courseDetails.instructor}</p>
              <p>Duration: {courseDetails.duration}</p>
              <p>Available Slots: {courseDetails.availableSlots}</p>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default CourseDetails;
