import { useState } from "react";

const CourseDetails = () => {
  const [courseDetails, setCourseDetails] = useState(null);
  const [courseID, setCourseID] = useState("");
  const [editMode, setEditMode] = useState("");
  const [editedDetails, setEditedDetails] = useState({
    title: "",
    description: "",
    instructor: "",
    duration: 0,
    availableSlots: 0,
  });

  const handleSearch = () => {
    try {
      fetch(`/api/courses/${courseID}`)
        .then((response) => {
          if (response.ok) {
            console.log("Course Existing");
            return response.json();
          }
          throw new Error("error response empty");
        })
        .then((data) => {
          setCourseDetails(data);
        });
    } catch (error) {
      console.lerror(error);
    }
  };

  return (
    <div>
      <h1>Course Details</h1>
      <input
        type="text"
        name="courseID"
        placeholder="Enter Course ID"
        value={courseID}
        onChange={(e) => setCourseID(e.target.value)}
      />
      <button onClick={handleSearch}>Search</button>
      {courseDetails && (
        <div>
          {editMode ? (
            <div>
                <input
                    type="text"
                    name="title"
                    value={courseDetails.title}
                    onChange={handleChange}
                />
                <input
                    type="text"
                    name="description"
                    placeholder="Course Description"
                    value={courseDetails.description}
                    onChange={handleChange}
                />
                <input
                    type="text"
                    name="instructor"
                    placeholder="Course Instructor"
                    value={courseDetails.instructor}
                    onChange={handleChange}
                />
                <input
                type="number"
                name="duration"
                placeholder="Class Duration"
                value={courseDetails.duration}
                onChange={handleChange}
                />
                <input
                type="number"
                name="availableSlots"
                placeholder="Available slots"
                value={courseDetails.availableSlots}
                onChange={handleChange}
                />
            </div>
          ) : (
            <div>
              <p>Title: {courseDetails.title}</p>
              <p>Description: {courseDetails.description}</p>
              <p>Instructor: {courseDetails.instructor}</p>
              <p>Duration: {courseDetails.duration}</p>
              <p>Available Slots: {courseDetails.availableSlots}</p>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default CourseDetails;
